<template>  <div class="wrapper">    <v-head></v-head>    <v-sidebar></v-sidebar>    <div class="content-box" :class="{'content-collapse':collapse}">      <div class="content">        <transition name="move" mode="out-in">          <keep-alive :include="tagsList">            <router-view></router-view>          </keep-alive>        </transition>      </div>    </div>  </div></template><script>  import vHead from  './Header'  import vSidebar from './Sidebar'  import bus from './bus';  export default {    components: {      vHead, vSidebar    },    data() {      return {        tagsList: [],        collapse: false      }    },    created(){      bus.$on('collapse', msg => {        this.collapse = msg;      });      // 只有在标签页列表里的页面才使用keep-alive，即关闭标签之后就不保存到内存中了。      bus.$on('tags', msg => {        let arr = [];        for(let i = 0, len = msg.length; i < len; i ++){          msg[i].name && arr.push(msg[i].name);        }        this.tagsList = arr;      })    },    mounted() {      this.getUserInfo()    },    methods:{      // 获取用户信息      getUserInfo() {        this.RestClient('GET_BASE', '/user/info').then((res) => {          if(res.data.code == 200) {            this.$store.commit('getUserInfo',{userInfo: res.data.data});          }        })      },    }  }</script>