<template>  <div class="sidebar"><!--unique-opened router-->    <el-menu class="sidebar-el-menu" :default-active="onRoutes" :collapse="collapse" background-color="#324157"             text-color="#bfcbd9" active-text-color="#20a0ff" router>      <template v-for="item in menus">        <template v-if="item.subs">          <el-submenu :index="item.router" :key="item.router">            <template slot="title">              <i :class="item.icon"></i><span slot="menu">{{ item.menu }}</span>            </template>            <template v-for="subItem in item.subs">              <el-submenu v-if="subItem.subs" :index="subItem.router" :key="subItem.router">                <template slot="menu">{{ subItem.menu }}</template>                <el-menu-item v-for="(threeItem,i) in subItem.subs" :key="i" :index="threeItem.router">                  {{ threeItem.menu }}                </el-menu-item>              </el-submenu>              <el-menu-item v-else :index="subItem.router" :key="subItem.router">                {{ subItem.menu }}              </el-menu-item>            </template>          </el-submenu>        </template>        <template v-else>          <template v-if="item.menu!=='消息管理'&&item.menu!=='数据监控'&&item.menu!=='个人设置'">          <el-menu-item :index="item.router" :key="item.router">            <template v-if="item.icon">              <i :class="item.icon"></i>            </template>              <template v-else>                <i class="el-icon-tickets"></i>              </template>            <span slot="title">{{ item.menu }}</span>          </el-menu-item>          </template>        </template>      </template>    </el-menu>  </div></template><script>  import cookie from 'js-cookie'  import bus from '../common/bus';  export default {    data() {      return {        collapse: false,        items: [          {            icon: 'el-icon-user',            router: 'userAdmin',            menu: '用户管理',          },          {            icon: 'el-icon-rank',            router: 'oneLevelAgent',            menu: '一级代理管理'          },          {            icon: 'el-icon-help',            router: 'twoLevelAgent',            menu: '二级代理管理'          },          {            icon: 'el-icon-coin',            router: 'walletAdmin',            menu: '代理资产管理'          },          {            icon: 'el-icon-s-operation',            router: 'device',            menu: '设备管理',          },          {            icon: 'el-icon-upload2',            router: 'coinAdmin',            menu: '提币管理'          },          {            icon: 'el-icon-s-tools',            router: 'config',            menu: '配置管理'          },          {            icon: 'el-icon-user',            router: 'setting',            menu: '个人设置'          },          {            icon: 'el-icon-cpu',            router: '4',            menu: 'log'          }        ]      }    },    computed:{      onRoutes(){        return this.$route.path.replace('/','');      },      menus() {        return JSON.parse(localStorage.getItem("menus"))      }    },    created(){      this.menus.splice(0,1);      // 通过 Event Bus 进行组件间通信，来折叠侧边栏      bus.$on('collapse', msg => {        this.collapse = msg;      })    }  }</script><style lang="scss" scoped>  .sidebar{    display: block;    position: absolute;    left: 0;    top: 70px;    bottom:0;    overflow-y: scroll;    .logo {      width: 230px;      line-height: 70px;      background-color: #324157;      text-align: center;      color: #fff;    }  }  .sidebar::-webkit-scrollbar{    width: 0;  }  .sidebar-el-menu:not(.el-menu--collapse){    width: 230px;  }  .sidebar > ul {    height:100%;  }</style>