<template>  <div class="block">    <vue-particles      color="#fff"      :particleOpacity="0.7"      :particlesNumber="60"      shapeType="circle"      :particleSize="4"      linesColor="#fff"      :linesWidth="1"      :lineLinked="true"      :lineOpacity="0.4"      :linesDistance="150"      :moveSpeed="2"      :hoverEffect="true"      hoverMode="grab"      :clickEffect="true"      clickMode="push"      class="particle">    </vue-particles>    <div class="loginBox">      <div class="logo">        <h1>云链管理后台</h1>        <p class="mgt10">基于区块链技术、去中心化数字资源分发交易网络</p>      </div>      <el-form ref="forms" :rules="rules" :model="form" class="form">        <p>邀请注册</p>        <el-form-item prop="iphone">          <el-input placeholder="11位手机号" v-model="form.iphone">            <template slot="prepend">+ 86</template>          </el-input>        </el-form-item>        <el-form-item>          <el-input placeholder="请输入邀请码" v-model="form.invateCode"></el-input>        </el-form-item>        <el-form-item>          <el-input v-model="form.code" placeholder="输入验证码" class="form_code"></el-input>          <el-button class="form_button" @click="sendCaptcha">获取验证码</el-button>        </el-form-item>        <el-form-item prop="password">          <el-input v-model="form.password" type="password" placeholder="6-16位密码，区分大小写"></el-input>        </el-form-item>        <el-form-item>          <el-input v-model="form.erpassword" type="password" placeholder="确认密码"></el-input>        </el-form-item>        <div class="form_flex">          <el-button type="primary" @click="submit('forms')">注册</el-button>          <router-link tag="span" to="/login" class="cursor">已有账号登录</router-link>        </div>      </el-form>    </div>    <div class="footer">      <p>copyright 2018 云链CSDC开发团队出品</p>    </div>  </div></template><script>	export default {		data() {			return {				form: {					account: '',					password: '',					iphone: '',					invateCode: '',					erpassword: '',					code: '',					delivery: true				},				rules: {					iphone: [						{							required: true,							message: '请输入手机号',							trigger: 'change'						},						{							required: true,							pattern: /^1[3456789]\d{9}$/,//可以写正则表达式呦呦呦							message: '手机号码有误',							trigger: 'change'						}],					password: [{						required: true,						message: '请输入密码',						trigger: 'change'					}]				}			}		},		methods: {			submit(form) {				this.$refs[form].validate((valid) => {					if (valid) {						let params = {							mobile: this.form.iphone,							type: 1,							captcha: this.form.code,							inviteCode: this.form.invateCode,							password: this.form.password						};						this.RestClient('CREATE', '/user/register', params).then((res) => {							if (res.data.code == 200) {								this.$message({									message: '注册成功'								});								this.$router.push({path: '/RegisterSuccess', query: {phone: this.form.iphone}})							}						})					}				})			},			sendCaptcha() {				if (!(/^1[3456789]\d{9}$/.test(this.form.iphone))) {					this.$message({						message: '手机号码有误！',						type: 'info'					});					return false;				}				let params = {					mobile: this.form.iphone,					type: 1				};				this.RestClient('GET_LIST', '/user/sendCaptcha', params).then((res) => {					if (res.data.code) {						this.$message({							message: '手机验证码发送成功',							type: 'success'						})					}				})			}		}	}</script><style lang="scss" scoped>  .block {    background: rgb(238, 239, 244);    height: 100%;    display: flex;    align-items: center;    font-size: 14px;    .particle {      position: absolute;      top: 0;      bottom: 0;      left: 0;      right: 0;    }    .loginBox {      z-index: 10;      margin: 0 auto;      width: 490px;      height: 500px;      .logo {        text-align: center;        p {          font-size: 15px;          color: rgb(130, 130, 130);        }      }      .form {        p {          font-size: 16px;          margin-bottom: 15px;        }        width: 80%;        margin: 0 auto;        margin-top: 60px;        .form_code {          width: 60%;        }        .form_button {          width: 36%;          margin-left: 4%;        }        .form_flex {          display: flex;          justify-content: space-between;          button {            width: 150px;          }          span {            line-height: 40px;            color: rgb(25, 120, 254);          }        }      }    }    .footer {      position: absolute;      bottom: 0;      left: 0;      right: 0;      color: rgb(124, 125, 128);      text-align: center;      padding-bottom: 15px;    }  }</style>